namespace $.$$ {

	$mol_style_define( $optimade_zero, {

		/* Шапка */
		$mol_page_head: {
			backgroundColor: 'transparent',
			boxShadow: 'none',
			backgroundImage: 'url(data:image/svg+xml;base64,)',
			paddingTop: '14vw',
			paddingBottom: '8vw',
			backgroundSize: 'auto 10vw',
			backgroundRepeat: 'no-repeat',
			backgroundPosition: 'center top',
			textAlign: 'center',
			fontSize: '12vh',
			letterSpacing: '-4px',
			color: '#555',
		},
		$mol_page_title: {
			display: 'block',
		},

		/* Блок с текстом */
		$mol_html_view_text: {
			maxWidth: '100vw'
		},
		/* Контейнер страницы результатов */
		Search_page: {
			flex: {
				basis: 'auto',
				grow: 1,
			},
			padding: '2vh 2vw',
			background: '#fff',
		},
		$optimade_zero_search_results: {
			display: 'grid',
			gridGap: '2vh',
			gridTemplateColumns: '1fr 1fr',
		},

		/* Стили для ячеек списка результатов */

		$optimade_zero_item: {
			minHeight: '10vh',
			display: 'flex',
			flexDirection: 'column',
		},
		Id: {
			padding: $mol_gap.text,
			// width: '7rem',
			// fontSize: '4vh',
		},

		Bib: {
			padding: $mol_gap.text,
			// width: '7rem',
			// fontSize: '4vh',
		},

		Property: {
			padding: $mol_gap.text,
			// fontSize: '3.5vh',
		},

		/* Сообщения об ошибке или отсутствии результатов */
		Search_error: {
			padding: '100px',
			margin: 'auto',
			color: 'red',
			textAlign: 'center',
		},

		Search_nothing_found: {
			padding: '100px',
			margin: 'auto',
			fontSize: '4vh',
			color: '#555',
			textAlign: 'center',
		},

		/* Дополнительное оформление строки результата */
		$mol_row: {
			margin: '1vh 0',
			padding: '1vh 0',
			borderBottom: '1px solid #ddd',
		},

	} )
}
